// import logo from './logo.svg';
import { useState } from 'react';
import './App.css';
// import About from './components/About';
import Navbar from './components/Navbar';
import Textform from './components/Textform';
import Alert from './components/Alert';


function App() {
  const [mode, setMode] = useState('light');
  const [alert, setAlert] = useState(null);

  // SHOW ALERT 
  // need to pass parameters in function showalert function
  const showAlert = (message,type)=>{
    setAlert({
      msg : message,
      type : type
    });

    setTimeout(()=>setAlert(null),5000);
  } 

  const toggleMode = ()=>{
    if(mode === "light"){
      setMode("dark");
      document.body.style.backgroundColor = "#2a676e";
      showAlert("Dark Mode is Enabled","success");
    }else{
      setMode("light");
      document.body.style.backgroundColor = "#ffffff";
      showAlert("Light Mode as been Enabled","success");
      }  
    }

  return (
    <>
    {/* <Navbar mainTitle = 'TextUtils' homeText= 'Home ' aboutText= 'About' /> */}
    <Navbar mainTitle = 'TextUtils'  mode ={mode} toggleMode={toggleMode} />
     {/* <Navbar/> */}
     <Alert alert= {alert} />
     <div className='container my-3'>
        <Textform taheading= 'Enter the text' mode ={mode} toggleMode={toggleMode} showAlert=
        {showAlert} />
        {/* <About/> */}
     </div>


    </>

  );
}

export default App;
