    if(mode === "light"){
      setMode("dark");

    }else{
      setMode("light");

      }  
    };

  return (
    <>
    {/* <Navbar mainTitle = 'TextUtils' homeText= 'Home ' aboutText= 'About' /> */}
    <Navbar mainTitle = 'TextUtils'  />
     {/* <Navbar/> */}

     <div className='container my-3'>
        <Textform taheading= 'Enter the text'  mode ={mode} toggleMode={toggleMode}/>
        {/* <About/> */}
     </div>


    </>

  );
}

export default App;
